package com.afsal.dev.dxplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.BaseRvItemBindingclass BaseCategoryAdapter(val categories :List<List<String>> ) :RecyclerView.Adapter<BaseCategoryAdapter.CategoryItemHolder>(){    class CategoryItemHolder(val binding: BaseRvItemBinding):RecyclerView.ViewHolder(binding.root) {    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): BaseCategoryAdapter.CategoryItemHolder {     return  CategoryItemHolder(BaseRvItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: BaseCategoryAdapter.CategoryItemHolder, position: Int) {      var category= categories[position]        val videosAdapter =VideosAdapter(category)        holder.binding.videosRv.apply {            layoutManager= LinearLayoutManager(context, LinearLayoutManager.HORIZONTAL, false)            adapter=videosAdapter        }    }    override fun getItemCount()=categories.size}