package com.afsal.dev.dxplayer.adaptersimport android.content.DialogInterfaceimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.BaseRvItemBindingimport com.afsal.dev.dxplayer.interfacess.OnItemClickListnerclass BaseCategoryAdapter(val categories :List<List<String>>,private  val listener:OnItemClickListner) :RecyclerView.Adapter<BaseCategoryAdapter.CategoryItemHolder>(){ inner   class CategoryItemHolder(val binding: BaseRvItemBinding):RecyclerView.ViewHolder(binding.root), View.OnClickListener {     init {         binding.showMore.setOnClickListener(this)     }     override fun onClick(v: View?) {         val position = absoluteAdapterPosition         if (position != RecyclerView.NO_POSITION) {          //   listener.onItemClick(position,)         }     } }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): BaseCategoryAdapter.CategoryItemHolder {     return  CategoryItemHolder(BaseRvItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: BaseCategoryAdapter.CategoryItemHolder, position: Int) {      var category= categories[position]        val videosAdapter =VideosAdapter(category)        holder.binding.videosRv.apply {            layoutManager= LinearLayoutManager(context, LinearLayoutManager.HORIZONTAL, false)            adapter=videosAdapter        }    }    override fun getItemCount()=categories.size}