package com.afsal.dev.dxplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.PlayListItemBindingimport com.afsal.dev.dxplayer.utills.CoreUttilesclass PlayListAdapter:RecyclerView.Adapter<PlayListAdapter.ItemHolder>() {     val list= listOf("list1","list2","list3","list4","list5","list5","list6")    class ItemHolder(val binding:PlayListItemBinding):RecyclerView.ViewHolder(binding.root) {       // fun bind()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {       return ItemHolder(PlayListItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {       val item=list[position]        holder.binding.textView4.text=item        holder.binding.rootLayout.setOnClickListener {            CoreUttiles.showSnackBar("clicked",holder.binding.rootLayout,""){}        }    }    override fun getItemCount()=list.size}