package com.afsal.dev.dxplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.PlayListItemBindingimport com.afsal.dev.dxplayer.models.audioSections.PlayListsimport com.afsal.dev.dxplayer.utills.CoreUttilesimport java.util.*class PlayListAdapter(private val onItemClick:(playList:PlayLists)->Unit):RecyclerView.Adapter<PlayListAdapter.ItemHolder>() {    private val diffCallBack= object :DiffUtil.ItemCallback<PlayLists>(){        override fun areItemsTheSame(oldItem: PlayLists, newItem: PlayLists): Boolean {            return oldItem.playListName==newItem.playListName        }        override fun areContentsTheSame(oldItem: PlayLists, newItem: PlayLists): Boolean {           return oldItem==newItem        }    }    val differ=AsyncListDiffer(this,diffCallBack) inner   class ItemHolder(val binding:PlayListItemBinding):RecyclerView.ViewHolder(binding.root) {        fun bind(playList: PlayLists){            binding.textView5.text=playList.playListName            binding.rootLayout.setOnClickListener {                // CoreUttiles.showSnackBar("clicked",holder.binding.rootLayout,""){}                onItemClick(playList)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {       return ItemHolder(PlayListItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {       val item=differ.currentList[position]               holder.bind(item)    }    override fun getItemCount()=differ.currentList.size}