package com.afsal.dev.dxplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.RecentVideoItemBindingimport com.afsal.dev.dxplayer.models.VideoSections.VideoItemModelimport com.afsal.dev.dxplayer.utills.CoreUttilesclass RecentVideoAdapter():RecyclerView.Adapter<RecentVideoAdapter.RecentVodItemHolder>() {    private val diffCallback=object :DiffUtil.ItemCallback<VideoItemModel>(){        override fun areItemsTheSame(oldItem: VideoItemModel, newItem: VideoItemModel): Boolean {            return oldItem.id==newItem.id        }        override fun areContentsTheSame(oldItem: VideoItemModel, newItem: VideoItemModel): Boolean {          return oldItem==newItem        }    }     val differ=AsyncListDiffer(this,diffCallback)    class RecentVodItemHolder(val binding: RecentVideoItemBinding):RecyclerView.ViewHolder(binding.root) {        fun bind(videoItem: VideoItemModel){            CoreUttiles.loadImageIntoView(videoItem.artUri,binding.recentVideoImage,null,CoreUttiles.IMAGE_FRAGMENT)        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): RecentVideoAdapter.RecentVodItemHolder {       return  RecentVodItemHolder(RecentVideoItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: RecentVideoAdapter.RecentVodItemHolder, position: Int) {        val videoItem=differ.currentList[position]                holder.bind(videoItem)    }    override fun getItemCount()=differ.currentList.size}