package com.afsal.dev.dxplayer.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.VideoItemBindingclass VideosAdapter(val vodList:List<String>) :RecyclerView.Adapter<VideosAdapter.ItemHolder>(){    class ItemHolder(val binding: VideoItemBinding):RecyclerView.ViewHolder(binding.root) {}    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VideosAdapter.ItemHolder {         return ItemHolder(VideoItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: VideosAdapter.ItemHolder, position: Int) {   val item=vodList[position]        holder.binding.duration.text=item    }    override fun getItemCount()=vodList.size}