package com.afsal.dev.dxplayer.adaptersimport android.text.format.DateUtilsimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.VideoItem1Bindingimport com.afsal.dev.dxplayer.models.VideoSections.VideoItemModelimport com.afsal.dev.dxplayer.utills.CoreUttilesclass VideosAdapter(val videosList:List<VideoItemModel>, private val onClick:((position:Int)->Unit)) :RecyclerView.Adapter<VideosAdapter.ItemHolder>(){ inner   class ItemHolder(val binding: VideoItem1Binding):RecyclerView.ViewHolder(binding.root) {        fun bind(video:VideoItemModel){            CoreUttiles.loadImageIntoView(video.artUri,binding.imageView,null, CoreUttiles.IMAGE_FRAGMENT)            binding.duration.text=DateUtils.formatElapsedTime(video.duration /1000)            binding.imageView.setOnClickListener{//                CoreUttiles.showSnackBar("item cliked",binding.root)               val position=absoluteAdapterPosition                if (position !=RecyclerView.NO_POSITION){                    onClick(position)                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VideosAdapter.ItemHolder {         return ItemHolder(VideoItem1Binding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: VideosAdapter.ItemHolder, position: Int) {                 holder.bind(videosList[position])    }    override fun getItemCount()=videosList.size}