package com.afsal.dev.dxplayer.adaptersimport android.text.format.DateUtilsimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.dxplayer.databinding.VideoItemBindingimport com.afsal.dev.dxplayer.models.VideoSections.VideoItemModelimport com.afsal.dev.dxplayer.utills.CoreUttilesclass VideosAdapter(val videosList:List<VideoItemModel>) :RecyclerView.Adapter<VideosAdapter.ItemHolder>(){    class ItemHolder(val binding: VideoItemBinding):RecyclerView.ViewHolder(binding.root) {        fun bind(video:VideoItemModel){            CoreUttiles.loadImageIntoView(video.artUri,binding.imageView,null, CoreUttiles.IMAGE_FRAGMENT)           binding.duration.text=DateUtils.formatElapsedTime(video.duration /1000)        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VideosAdapter.ItemHolder {         return ItemHolder(VideoItemBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: VideosAdapter.ItemHolder, position: Int) {                 holder.bind(videosList[position])    }    override fun getItemCount()=videosList.size}