package com.afsal.dev.dxplayer.dataBaseimport androidx.room.*import com.afsal.dev.dxplayer.models.VideoSections.PlayedVideoItem@Daointerface VideoHistoryDao {   @Insert (onConflict = OnConflictStrategy.REPLACE)   suspend fun insertVideoToHistory(video:PlayedVideoItem)   @Query("SELECT * FROM  recently_Played_items")   suspend fun getLastWatchedVideos():List<PlayedVideoItem>   @Query("DELETE FROM recently_Played_items WHERE videoId = :videoId")   suspend fun deleteSingleItemFromHistory(videoId: Long)   @Query("DELETE  FROM recently_Played_items")   suspend fun clearHistory()   @Update    suspend fun updatedCurrentVideo(video:PlayedVideoItem)    @Query("SELECT EXISTS (SELECT 1 FROM recently_Played_items WHERE videoId = :id)")    suspend fun checkItemExist(id: Long) :Boolean}