package com.afsal.dev.dxplayer.models.audioSectionsimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "play_list_song_table")data class PlayListWithSongs(    @PrimaryKey(autoGenerate = false)    val id: Long,    val tittle: String,    val album: String,    val artist: String,    val duration: Long,    val imageUri: String,    val artUri: String,    val folderName: String,    val PlayListName: String,    )