package com.afsal.dev.dxplayer.repositoryimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport com.afsal.dev.dxplayer.dataBase.MusicDaoimport com.afsal.dev.dxplayer.models.audioSections.MusicItemimport com.afsal.dev.dxplayer.models.audioSections.PlayListsimport com.afsal.dev.dxplayer.models.audioSections.PlayListWithSongsclass MusicRepository(private val musicDao: MusicDao) {    val allPlayList:LiveData<List<PlayLists>> =musicDao.getAllPlayList()    val playListData=MutableLiveData<List<PlayListWithSongs>>()    suspend fun  createPlayList(playLists: PlayLists){        musicDao.createPlayList(playLists)    }    //song data added to playlist-table and music-table    suspend fun addSongToPlayList(song:MusicItem,playListId:String){        musicDao.insertSongIntoSongsTable(song)        val playListWithSong=PlayListWithSongs(0,playListId,song.id)        musicDao.insertSongsIntoPlayList(playListWithSong)    }    suspend fun getAllSongsIds(playListId:String){      val data=  musicDao.getAllSongsInPlayList(playListId)        playListData.postValue(data)    }}