package com.afsal.dev.dxplayer.repositoryimport android.util.Logimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport com.afsal.dev.dxplayer.dataBase.MusicDaoimport com.afsal.dev.dxplayer.models.audioSections.PlayListWithSongsimport com.afsal.dev.dxplayer.models.audioSections.PlayListsclass MusicRepository(private val musicDao: MusicDao) {    //  private val _playListNames=MutableLiveData<List<PlayLists>>()    val allPlayListNames: LiveData<List<PlayLists>> = musicDao.getAllPlayList()    //  get() = _playListNames    val playListData = MutableLiveData<List<PlayListWithSongs>>()    init {        //getPlaylist()    }    fun getPlaylist() {        val list = musicDao.getAllPlayList()        Log.d("PPP ", "Repo Playlist ${list.value}")        //  _playListNames.value=list.value        //  _playListNames.postValue(list.value)    } suspend   fun checkSongExistence(id:Long,playList:String) =musicDao.existsSong(id,playList)    suspend fun createPlayList(playLists: PlayLists) {        musicDao.createPlayList(playLists)    }     suspend fun updatePlayListWithSongCount(playLists: PlayLists){         musicDao.updateSongCountInPlayList(playLists)     }    //song data added to playlist-table and music-table    suspend fun addSongToPlayList(song: PlayListWithSongs) {        musicDao.insertSongsIntoPlayList(song)    }    suspend fun getAllSongsInPlayList(playListName: String) =        musicDao.getAllSongsInPlayList(playListName)    suspend fun deleteSongFromPlayList(songId:Long,playListName: String) =        musicDao.deleteSongFromPlayList(songId,playListName)    suspend fun deletePlayList(playListName:String){        musicDao.deleteAllSongsFromPlayList(playListName)        musicDao.deletePlaylistNameFromPlayList(playListName)        getPlaylist()    }    suspend fun getPlayListData(playListName: String)= musicDao.getPlayListData(playListName)}