package com.afsal.dev.dxplayer.repositoryimport com.afsal.dev.dxplayer.dataBase.VideoHistoryDaoimport com.afsal.dev.dxplayer.models.VideoSections.PlayedVideoItemclass VideoRepository(private val videoHistoryDao: VideoHistoryDao) {    suspend fun addVideoToHistory(video:PlayedVideoItem)=videoHistoryDao.insertVideoToHistory(video)    suspend fun getLastWatchedVideos()=videoHistoryDao.getLastWatchedVideos()    suspend fun deleteSingleVideoFromHistory(videoId:Long)=videoHistoryDao.deleteSingleItemFromHistory(videoId)    suspend fun clearVideoHistory()=videoHistoryDao.clearHistory()    suspend fun updateVideoData(video:PlayedVideoItem)=videoHistoryDao.updatedCurrentVideo(video)    suspend fun checkItemExistOrNot(videoId: Long)=videoHistoryDao.checkItemExist(videoId)}