package com.afsal.dev.dxplayer.ui.fragmentsimport android.os.Bundleimport android.view.Viewimport androidx.fragment.app.Fragmentimport com.afsal.dev.dxplayer.ui.activities.DashBordActivityabstract class BaseFragment:Fragment() {    protected open var bottomNavigationViewVisibility = View.VISIBLE    private val dashBordActivity = activity as DashBordActivity    @Deprecated("Deprecated in Java")    override fun onActivityCreated(savedInstanceState: Bundle?) {        super.onActivityCreated(savedInstanceState)        if (activity is DashBordActivity) {            dashBordActivity.setBottomNavigationVisibility(bottomNavigationViewVisibility)        }    }    override fun onResume() {        super.onResume()        if (activity is DashBordActivity){            dashBordActivity.setBottomNavigationVisibility(bottomNavigationViewVisibility)        }    }}