package com.afsal.dev.dxplayer.ui.servicesimport android.app.Serviceimport android.content.Intentimport android.os.IBinderimport com.afsal.dev.dxplayer.models.audioSections.MusicItemimport com.google.android.exoplayer2.DefaultLoadControlimport com.google.android.exoplayer2.DefaultRenderersFactoryimport com.google.android.exoplayer2.ExoPlayerimport com.google.android.exoplayer2.MediaItemimport com.google.android.exoplayer2.trackselection.AdaptiveTrackSelectionimport com.google.android.exoplayer2.trackselection.DefaultTrackSelectorclass MusicService : Service() {    private lateinit var exoPlayer: ExoPlayer    private lateinit var trackSelector: DefaultTrackSelector    override fun onBind(intent: Intent?): IBinder? {        TODO("Not yet implemented")    }    fun initPlayer() {        //  trackSelector= DefaultTrackSelector(this)        exoPlayer = ExoPlayer.Builder(this)            .setSeekBackIncrementMs(5000)            .setSeekForwardIncrementMs(5000)            .build()    }    private fun setMediaItem(song:MusicItem){        if (exoPlayer!= null){            val mediaItem = MediaItem.fromUri(song.artUri)            exoPlayer.addMediaItem(mediaItem)            exoPlayer.prepare()            exoPlayer.play()        }    }}